          title={
            <Space>
              <HeartOutlined className="text-red-500" />
              <span>兴趣爱好</span>
            </Space>
          }
          style={{
            borderRadius: 16,
            border: 'none',
            boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)',
          }}
        >
          <Space wrap size={[16, 16]}>
            {['📚 阅读', '📷 摄影', '🎮 游戏', '🎵 音乐', '✈️ 旅行', '🏃 跑步', '☕ 咖啡', '🎬 电影'].map(hobby => (
