                    >
                      <Button
                        type="primary"
                        ghost
                        icon={<ThunderboltOutlined />}
                        loading={generatingCover}
                      >
                        自动生成 <DownOutlined />
                      </Button>
                    </Dropdown>
                    {coverList.length > 0 && (
                      <Dropdown
                        menu={{ items: coverThemeMenuItems }}
